import { useState } from "react";
import {storage} from "./firebase-config"
import { ref,uploadBytes,getDownloadURL} from "firebase/storage";
import {createWorker} from 'tesseract.js';
import FileUploadOutlinedIcon from '@mui/icons-material/FileUploadOutlined';
import './Homepagefire.css';


function Homepagefire() {
    const[imageUpload,setImageUpload]=useState(null);
    const [imageUrl, setImageUrl] = useState(null);
    const [stringValue, setStringValue] = useState('dharun');
    
    var imageRef="";
    const uploadImage=()=>{
        if(imageUpload==null) 
            return; 
        imageRef=ref(storage, `images/${imageUpload.name}`);
        uploadBytes(imageRef,imageUpload).then((snapshot)=>{
            alert("Image uploaded Successfully");
            getDownloadURL(snapshot.ref).then((url) => {
                setImageUrl(url);
                setStringValue('');
                
              });
        });    
    };

    const imageToText=async()=>{

        const worker = createWorker({
            logger: m => console.log(m),
          });

          
            await worker.load();
            await worker.loadLanguage('eng');
            await worker.initialize('eng');
            const { data: { text } } = await worker.recognize(imageUrl);
            setStringValue(text);    
    }

    const onFileChange = (event) => {
        setImageUpload(event.target.files[0]);
      };

    return(
        <body>
            <section>
                <div className="message">
                    <input type="file" onChange={onFileChange} />
                    {/*<button onClick={uploadImage}>Upload Image</button>*/}
                    <FileUploadOutlinedIcon className="svg_home" onClick={uploadImage}/> 
                    {imageUrl&&<img src={imageUrl} alt=""/>}
                </div>
                <button onClick={imageToText}>Generate text
                    
                    {
                        stringValue==="" ? <div className="loader"></div>:<h5>{stringValue}</h5>
                    }
                
                </button>
                
            </section> 
            <section>
                <div className="message">
            </div>              
            </section>     
        </body>
  
    )   
}

export default Homepagefire;
