import { useState, useEffect } from 'react';
import Button from '@mui/material/Button';
//import Box from '@mui/material/Box';
import './App.css';

const App = () => {
  {/*const [selectedImage, setSelectedImage] = useState(null);
  const [imageUrl, setImageUrl] = useState(null);

  useEffect(() => {
    if (selectedImage) {
      setImageUrl(URL.createObjectURL(selectedImage));
    }
  }, [selectedImage]);
*/}
  return (
    <div>
        <title>Python Flask Upload and display image</title>
        <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.6/css/bootstrap.min.css" />        
        <p /><h1 align="center">Python Flask Upload and display image</h1><p />
        <div className="container">
          <div className="row">
            <h2>Select a file to upload</h2>
            <p>
              {'{'}% with messages = get_flashed_messages() %{'}'}
              {'{'}% if messages %{'}'}
            </p><ul>
              {'{'}% for message in messages %{'}'}
              <li>{'{'}{'{'} message {'}'}{'}'}</li>
              {'{'}% endfor %{'}'}
            </ul>
            {'{'}% endif %{'}'}
            {'{'}% endwith %{'}'}
            <p />
            {'{'}% if filename %{'}'}
            <div>
              <img src="{{ url_for('display_image', filename=filename) }}" />
            </div>
            {'{'}% endif %{'}'}
            <form method="post" action="/" encType="multipart/form-data">
              <dl>
                <p>
                  <input type="file" name="file" className="form-control" autoComplete="off" required />
                </p>
              </dl>
              <p>
                <input type="submit" defaultValue="Submit" className="btn btn-info" />
              </p>
            </form>
          </div>
        </div>
      </div>
  );
};

export default App;
